import React from "react";
import { useNavigate } from "react-router-dom";
import logo from "../../style/126510.png";
import login from "../../style/login-icon.png";
import produtos from "../../dados/produtos.json";

export default function Home() {
  const [products, setProducts] = React.useState([]);
  const [currentsProduct, setCurrentUser] = React.useState("");
  const navigate = useNavigate();

  React.useEffect(() => {
    //setProducts(produtos);
    fetch("https://localhost:3000/products/all")
    .then((response) => response.json())
    .then((data) => console.log(data));
  }, []);

  const handleClick = (event) => {
    navigate(`/Description/${event.currentTarget.id}`)
  };

  const handleLoad = (event) => {
    console.log("funci");
  };

  return (
    <div>
      <header>
        <a id="home" href="./index.html">
          <h1>Tenis Store</h1>
        </a>
        <div id="divBusca">
          <input type="text" id="txtBusca" placeholder="Buscar..." />
          <button id="btnBusca">Buscar</button>
        </div>
        <div>
          <a href="./carrinho.html">
            <img id="cart-icon" src={logo} alt="" srcset="" />
          </a>
          <a href="./login.html">
            <img id="login-icon" src={login} alt="" />
          </a>
        </div>
      </header>
      <li id="menu-content">
        <ol>Todas as categorias</ol>
        <ol>Ofertas</ol>
        <ol>Mais vendidos</ol>
        <ol>Outlet</ol>
      </li>
      <section>
        {products.map((product) => (
          <div id={`${product.id}`} className="tenis-content" onClick={handleClick}>
            <a href="">
              <img className="image" src={`${product.imagem}`} alt="" href="" />
            </a>
            <a href="" className="name">
              {`${product.nome}`}
            </a>
            <p className="genero">{`${product.genero}`}</p>
            <p className="frete">Frete Gratis</p>
            <p>{`${product.valor}`}</p>
            <p className="parcelas">{`${product.parcela}`}</p>
          </div>
        ))}
      </section>
      <div className="f1">
        <footer className="id1">
          <ul className="list-footer">
            <li className="nav-item">Home</li>
            <li className="nav-item">Fale Conosco</li>
            <li className="nav-item">Cadastro</li>
            <li className="nav-item">Política de Privacidade</li>
          </ul>
          <div className="f2"></div>
          <p className="txt-center">© 2022 Company, Inc</p>
        </footer>
      </div>
    </div>
  );
}
